import React, { Component } from 'react'
import SpeakerDropdown from './SpeakerDropdown';
import { Textarea } from 'react-materialize';

class ScriptBuilder extends Component {
  state = {
    activeSpeaker: {},
    spokenText: ''
  }

  handleSpeakerChange = (activeSpeaker) => {
    this.setState({activeSpeaker});

  }

  render() {
    return (
      <div className='container '>
        <div className='row'>

            <div className="row card-panel grey lighten-1">
              <h3>Line 0</h3>
              <div className="col m12 s6">
                <label>Speaker</label>
                <SpeakerDropdown 
                  activeSpeaker={this.state.activeSpeaker}
                  availableSpeakers={this.props.availableSpeakers}
                  onChange={this.handleSpeakerChange}
                />
              </div>
              <div className="col m12 s6">
                <h6>Spoken Text</h6>
                <Textarea
                  m={12}
                  placeholder="type speaker text here"
                />
              </div>
              <div className="row">
                <div className="col s6">

                </div>
                <div className="col s6">

                </div>
              </div>

            </div>

        </div>   
      </div>
    )
  }
}

export default ScriptBuilder;
