import React, { Component } from 'react'
import SpeakerDropdown from './SpeakerDropdown';
import { Textarea } from 'react-materialize';
import LeftParticipantDropdown from './LeftParticipantDropdown';
import RightParticipantDropdown from './RightParticipantDropdown';
import AddNewLineButton from './AddNewLineButton';

class ScriptBuilder extends Component {
  state = {
    activeSpeaker: {},
    spokenText: ''
  }

  handleSpeakerChange = (activeSpeaker) => {
    this.setState({activeSpeaker});

  }

  render() {
    return (
      <div className='container '>
        <div className='row'>

            <div className="row card-panel grey lighten-1">
              <h3>Line 0</h3>
              <div className="col m12 s6">
                <h6>Speaker</h6>
                <SpeakerDropdown 
                  activeSpeaker={this.state.activeSpeaker}
                  availableSpeakers={this.props.availableSpeakers}
                  onChange={this.handleSpeakerChange}
                />
              </div>
              <div className="col m12 s6">
                <h6>Spoken Text</h6>
                <Textarea
                  m={12}
                  placeholder="type speaker text here"
                />
              </div>
              <div className="row">
                <div className="col s6">
                  <h6>Left Particpants</h6>
                  <LeftParticipantDropdown/>
                </div>
                <div className="col s6">
                  <h6>Right Participants</h6>
                  <RightParticipantDropdown/>
                </div>
              </div>
              <AddNewLineButton onClick={this.props.addNewLine}/>

            </div>

        </div>   
      </div>
    )
  }
}

export default ScriptBuilder;
