import React, { Component } from "react";
import 'materialize-css';
import { Button, Card, Row, Col, CardPanel} from 'react-materialize';
import InitDialogueForm from "./InitDialogueForm";

class DialogueForm extends Component {
  constructor() {
    super();
    this.state = {
      scriptName: '',
      leftParticipants: [],
      rightParticipants: [],
      scriptChunks: []
    }

    this.handleLeftDropdownChange = this.handleLeftDropdownChange.bind(this);
    this.handleRightDropdownChange = this.handleRightDropdownChange.bind(this);
  }

  handleInputChange = e => {
    let {value, name} = e.target;
    this.setState({
      ...this.state,
      [name]: value
    });
    console.log(this.state)
  }

  handleLeftDropdownChange(name, option) {
    this.setState(state => {
      console.log(option)
      return {
        leftParticipants: option
      };
    });
  };

  render() {
    return(
      <>
       <div className='container'>
         <div className='row center'>
           <h1 className='white-text'>Callisto Dialogue Tool</h1>
         </div>
         <div className='row'>
            <InitDialogueForm
              onInputChange={this.handleInputChange}
              onLeftDropdownChange={this.handleLeftDropdownChange}
              onRightDropdownChange={this.handleRightDropdownChange}
              scriptName={this.state.scriptName}
              leftParticipants={this.state.leftParticipants}
              rightParticipants={this.state.rightParticipants}
            />
         </div>
       </div>
      </>
    );
  }

}

export default DialogueForm;
